yAxis.calcFunction:
var a = sum(WM Consumer.consumption);
var b = sum(WM Region.full_consumption);
var leaked = b - a;
var percent = (leaked * 100) / b;
if (percent < 0) {
    return -percent;
}
return percent;

yAxis.parsedFunction:
var leaked = row['full_consumptionSUM'] - row['consumptionSUM'];
var percent = leaked*100/row['full_consumptionSUM'];
return percent;

xAxis.calcFunction:
var leaked = sum(WM Region.full_consumption) - sum(WM Consumer.consumption);
var percent = leaked*100/sum(WM Region.full_consumption);
return percent;

xAxis.parsedFunction:
var leaked = row['full_consumptionSUM'] - row['consumptionSUM'];
var percent = leaked*100/row['full_consumptionSUM'];
return percent;

xAxis.calcFunction:
var leaked = sum(WM Region.full_consumption) - sum(WM Consumer.consumption);
var pricePerKube = 0.213;
var formatted = parseFloat((leaked * pricePerKube).toFixed(2));
return formatted;

xAxis.parsedFunction:
var leaked = row['full_consumptionSUM'] - row['consumptionSUM'];
var pricePerKube = 0.213;

var formatter = new Intl.NumberFormat('en-US', {
  style: 'currency',
  currency: 'USD',
});

return formatter.format(leaked*pricePerKube);
