descriptor.controllerScript:
let statesArr = [];
let stateObject, statesValue;

self.onInit = function() {
    let stateController = self.ctx.$scope.ctx.stateController;
    if(stateController) {
        stateObject = stateController.stateObject;
        statesValue = stateController.statesValue;
    }

    for(let i = 0; i < stateObject.length; i++) {
        let newObj = {};
        newObj.name = statesValue[stateObject[i].id].name;
        newObj.entityName = stateObject[i].params.entityName;
        if(newObj.name) {
            let newObjNameArr = newObj.name.split(' ');
            for(let j = 0; j < newObjNameArr.length; j++) {
                if(newObjNameArr[j].includes('$')) {
                    let entityName = newObjNameArr[j].split(':');
                    if(entityName.length === 2) {
                        newObj.name = newObj.name.replace(/\$\{\w+\:\w+\}/g, stateObject[i].params[entityName[0].substring(2)].entityName);
                    } else {
                        newObj.name = newObj['name'].replace(/\$\{\w+\}/g, newObj.entityName);
                    }
                }
            }
        }
        newObj.id = stateObject[i].id;
        newObj.entityName = stateObject[i].params.entityName;
        statesArr.push(newObj);
    }

    self.ctx.$scope.statesArr = statesArr;
    console.log(statesArr)

    self.ctx.$scope.navToState = function(idx) {
        stateController.navigatePrevState(idx);
    }
// *ngClass="i+1 !== statesArr.length ? 'active': ''"
}
self.typeParameters = function() {
    return {
        dataKeysOptional: true,
        datasourcesOptional: true
    };
}
self.actionSources = function() {
    return {
        'elementClick': {
            name: 'widget-action.element-click',
            multiple: true
        }
    };
}

self.onDestroy = function() {
}

