descriptor.controllerScript:
let myChart, settings, utils, interval;

let option = {
    xAxis: {
        type: 'category',
        show: false
    },
    yAxis: {
        type: 'value',
        show: false,
        data: []
    },
    series: [{
        data: [],
        type: 'line',
        // showSymbol: false
    }],
    // grid: {
    //     left: '0',
    //     right: '0',
    //     bottom: '0',
    //     top: '0',
    // }
};

let ranges = [{
        name: 'This week',
        currentRange: [
            moment().endOf('week'), moment()
            .startOf('week')
        ],
    },
    {
        name: 'Last week',
        currentRange: [
            moment().subtract(1, 'weeks').endOf(
                'week'),
            moment().subtract(1, 'weeks').startOf(
                'week')
        ],
    },
    {
        name: 'This month',
        currentRange: [
            moment().endOf('month'), moment()
            .startOf('month')
        ],
    },
    {
        name: 'Last month',
        currentRange: [
            moment().subtract(1, 'months').endOf(
                'month'),
            moment().subtract(1, 'months').startOf(
                'month')
        ],
    }
];

function toTimestamp(strDate) {
    return Date.parse(strDate);
}

function numberWithSpaces(num) {
    return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g,
        " ");
}

function getColor(value) {
    if (value > 0) {
        return '#6EBC30';
    } else if (value < 0) {
        return '#D44037';
    } else if (value === 0) {
        return '#0C1297';
    }
}

self.onInit = function() {
    let divId = 'horizontalBarChart_' + self.ctx.widget
        .id;
    let container = document.getElementsByClassName(
        'chart');
    container[0].setAttribute('id', divId);
    self.ctx.$scope.data = self.ctx.defaultSubscription
        .data;

    myChart = echarts.init(container[0]);
    self.ctx.myChart = myChart;

    option && myChart.setOption(option);
    self.ctx.updateWidgetParams();

    self.ctx.$scope.title = self.ctx.data[0].dataKey
        .label;

    self.ctx.$scope.ranges = ranges;
    self.ctx.$scope.rangesForm = self.ctx.$scope.fb
        .group({
            selectedRange: [ranges[0].currentRange,
                self.ctx.$scope.validators
                .required
            ]
        });

    self.ctx.$scope.onChange = function(value) {
        let startDate = toTimestamp(value[1]._d);
        let endDate = toTimestamp(value[0]._d);
        self.ctx.$scope.range = ranges.find(el => el.currentRange === value).name;
        console.log(self.ctx.$scope.range)
        self.ctx.dashboard.onUpdateTimewindow(
            startDate, endDate, 86400000);

        let idx = ranges.findIndex(el => el
            .currentRange === value);

        self.ctx.$scope.currentRangeName = ranges[
            idx].name;

        self.ctx.detectChanges();
    }

    self.ctx.$scope.onChange(ranges[0].currentRange);
}

self.onDataUpdated = function() {
    option.series[0].data = [];
    for (let i = 0; i < self.ctx.$scope.data
        .length; i++) {
        for (let j = 0; j < self.ctx.$scope.data[i].data
            .length; j++) {
            option.series[0].data.push(self.ctx.$scope
                .data[i].data[j][1]);
        }
    }

    myChart.setOption(option);
    self.ctx.detectChanges();
}

self.onLatestDataUpdated = function() {
    if (self.ctx.latestData.length && self.ctx.latestData[1].data[0]) {
        self.ctx.$scope.totalValue = Math.round(self.ctx.latestData[0].data[0][1]) + self.ctx.latestData[0].dataKey.units;

        let rangeDiff = Math.round(self.ctx.latestData[1].data[0][1]);
        if (rangeDiff > 0) {
            rangeDiff = '+' + rangeDiff;
        }
        self.ctx.$scope.rangeDiff = rangeDiff;

        let color = getColor(rangeDiff);
        option.color = color;
        self.ctx.$scope.color = color
    }

    myChart.setOption(option);
    self.ctx.detectChanges();
}

self.onResize = function() {
    myChart.resize();
}


self.onDestroy = function() {
    myChart.clear();
}

self.typeParameters = function() {
    return {
        hasAdditionalLatestDataKeys: true
    };
}

