descriptor.controllerScript:
self.onInit = function() {
    let $injector = self.ctx.$scope.$injector;
    let entityGroupService = $injector.get(self.ctx
        .servicesMap.get('entityGroupService'));

    if (self.ctx.$scope.ctx.stateController) {
        let currentState = self.ctx.$scope.ctx
            .stateController.stateObject[0].id;

        let navItems = self.ctx.$container.find(
            '.navItem');
        let size = navItems.length;
        for (let i = 0; i < size; i++) {
            if (navItems[i].id.includes(currentState)) {
                navItems[i].classList.add('active')
            } else if (navItems[i].innerText
                .toLowerCase().includes('home') &&
                currentState == 'default') {
                navItems[i].classList.add('active')
            }

        }


        self.ctx.$scope.onNavClick = function(event,
            actionName) {
            let element;
            let activeMenu = self.ctx.$container
                .find('.active');

            for (let i = 0; i < activeMenu
                .length; i++) {
                activeMenu[i].classList.remove(
                    'active')
            }
            if (event.target.tagName
            .toLowerCase() != 'div') {
                element = event.target.closest(
                    'div');
            } else {
                element = event.target;
            }

            element.classList.add('active');

            let params = self.ctx.$scope.ctx
                .stateController.getStateParams();
            let actionEventDescriptors = self.ctx
                .$scope.ctx.actionsApi
                .getActionDescriptors(
                    'elementClick');
            let actionEvent;

            if (actionEventDescriptors.length) {
                actionEventDescriptors.forEach(
                    action => {
                        if (action.name ==
                            actionName) {
                            actionEvent =
                            action;
                        }
                    });

                setTimeout(() => {
                    self.ctx.$scope.ctx
                        .actionsApi
                        .handleWidgetAction(
                            null,
                            actionEvent,
                            params.entityId,
                            params
                            .entityName);

                }, 0);
            }

        }
    }

}

self.actionSources = function() {
    return {
        'elementClick': {
            name: 'widget-action.element-click',
            multiple: true
        }
    };
}

self.typeParameters = function() {
    return {
        datasourcesOptional: true,
        dataKeysOptional: true
    };
}

self.onResize = function() {

}

self.onDestroy = function() {}

