let tabsEntities = []

self.onInit = function() {
    let stateId = self.ctx.stateController.getStateId();
    let stateParams = self.ctx.stateController
        .getStateParams();
    console.log(self.ctx.datasources)
    self.ctx.datasources.forEach(entity => {
        let newEntity = {};
        newEntity.name = entity.entity.name;
        newEntity.id = entity.entity.id;

        tabsEntities.push(newEntity)
    })
    console.log(tabsEntities)
    self.ctx.$scope.tabsEntities = tabsEntities;
    self.ctx.detectChanges();
    console.log('CONTEXT', self.ctx)
    self.ctx.$scope.chooseEntity = function(target,
        entity) {

        let element;
        let activeMenu = self.ctx.$container.find(
            '.active');

        for (let i = 0; i < activeMenu
            .length; i++) {
            activeMenu[i].classList.remove('active')
        }

        if (target.tagName.toLowerCase() !==
            'div') {
            element = target.closest('div');
        } else {
            element = target
        }

        element.classList.add('active');

        let stateParams = self.ctx.stateController
            .getStateParams()
        stateParams.selectedGreenhouse = {
            entityId: entity.id,
            entityName: entity.name,
            entityLabel: entity.name
        }
        self.ctx.stateController.updateState(
            stateId, stateParams);
            console.log('CHOSEN ENTITY', stateParams)
        self.ctx.detectChanges();
    }

    let tabs = self.ctx.$container.find('.tab');
    if ('selectedGreenhouse' in stateParams) {
        for (let i = 0; i < tabs.length; i++) {
            if(tabs[i].innerText === stateParams.selectedGreenhouse.entityName) {
                tabs[i].classList.add('active')
            }
        }
    } else {
        self.ctx.$scope.chooseEntity(tabs[0], tabsEntities[0])
        console.log('CHOSE ENTITY', tabs[0], tabsEntities[0])
    }
}

self.typeParameters = function() {
    return {
        dataKeysOptional: true
    };
}

self.actionSources = function() {
    return {
        'elementClick': {
            name: 'widget-action.element-click',
            multiple: true
        }
    };
}

self.onDestroy = function() {}

