descriptor.controllerScript:
let settings, utils, $scope, aliasController, filter;
// dashboard filter params: (active equal False or active equal True) and alarms greater or equal 0
// !!!!IMPORTANT dashboard type in 'electricity,water meter,temperature,gas,co2' and filter params(active equal False or active equal True) and name contains "" ingnore case'

let sensorTypes = [{
    value: 'All',
    key: 'All',
    chosen: true
}];
let statuses = [{
        key: "Connected",
        value: "connected",
        chosen: true
    },
    {
        key: "Inactive",
        value: "inactive",
        chosen: true
    }
];
let greenhouses = [
    {
        key: "All",
        value: "All",
        chosen: true
    }
];

self.onInit = function() {
    utils = self.ctx.$scope.$injector.get(self.ctx
        .servicesMap.get('utils'));
    settings = utils.deepClone(self.ctx.settings) || {};

    if (self.ctx.dashboard.dashboardWidgets
        .parentDashboard) {
        aliasController = self.ctx.dashboard
            .dashboardWidgets.parentDashboard
            .aliasController;
    } else {
        aliasController = self.ctx.dashboard
            .aliasController;
    }
    if (aliasController.userFilters && Object.values(
            aliasController.userFilters).find(el => el
            .filter === 'Greenhouses Sensors')) {
        for (let localFilter in aliasController
                .userFilters) {
            if (aliasController.origFilters[localFilter]
                .filter === 'Greenhouses Sensors') {
                filter = aliasController.userFilters[
                    localFilter];
                filter = JSON.parse(JSON.stringify(
                    filter));
            }
        }
    } else if (aliasController.origFilters) {
        for (let localFilter in aliasController
                .origFilters) {
            if (aliasController.origFilters[localFilter]
                .filter === 'Greenhouses Sensors') {
                filter = aliasController.origFilters[
                    localFilter];
                filter = JSON.parse(JSON.stringify(
                    filter));
            }
        }
    }

    $scope = self.ctx.$scope;

    self.ctx.data.forEach(dataItem => {
        if(dataItem.dataKey.name === 'type') {
            if (dataItem.data[0]) {
                let newFilter = {
                    chosen: true
                };
                newFilter.value = dataItem.data[0][1];
                newFilter.key = dataItem.data[0][1].split(' ').splice(1).join(' ');
                if (!sensorTypes.find(e => e.value === newFilter.value)) {
                    sensorTypes.push(newFilter);
                }
            }
        } else if(dataItem.dataKey.name === 'from_greenhouse') {
            if(dataItem.data[0]) {
                let newFilter = {
                    chosen: true,
                    value: dataItem.data[0][1],
                    key: dataItem.data[0][1]
                }
                if(!greenhouses.find(e => e.value === newFilter.value)){
                    greenhouses.push(newFilter);
                }
            }
        }
    })

    self.ctx.$scope.sensorTypes = sensorTypes;
    self.ctx.$scope.statuses = statuses;
    self.ctx.$scope.greenhouses = greenhouses;

    $scope.onFilterChange = function(filterItem, filterName) {
        filterItem.chosen = !filterItem.chosen;

        if(filterName === 'sensorType') {
            if(filterItem.value === 'All') {
                sensorTypes.forEach(item => item.chosen = filterItem.chosen);
                console.log(sensorTypes)
            } else {
                console.log('TYPES', sensorTypes)
                if(sensorTypes.find(e => e.chosen === false)) {
                    console.log('here')
                    sensorTypes.find(e => e.value === 'All').chosen = false
                }
            }
        }

        if(filterName === 'greenhouses') {
            if(filterItem.value === 'All') {
                greenhouses.forEach(item => item.chosen = filterItem.chosen);
            } else {
                if(greenhouses.find(e => e.chosen === false)) {
                    greenhouses.find(e => e.value === 'All').chosen = false
                }
            }
        }

        let sensors = sensorTypes.filter(item =>
            item.value !== 'All');
        let greenhousesStr = greenhouses.filter(item => item.value !== 'All');

        let selectedSensors = sensors.filter(
            item => item.chosen).map(item =>
            item.value).join(',');
        if(!selectedSensors.length) {
            selectedSensors = '-'
        }

        let selectedGreenhouses = greenhousesStr.filter(item => item.chosen).map(item => item.value).join(',');
        if(!selectedGreenhouses.length) selectedGreenhouses = '-'

        let connected = statuses[0].chosen;
        let inactive = statuses[1].chosen;

        if (filter && filter.keyFilters) {
            filter.keyFilters[1].predicates[0]
                .keyFilterPredicate.value
                .defaultValue = selectedSensors;
            filter.keyFilters[2].predicates[0]
                .keyFilterPredicate.predicates[0]
                .keyFilterPredicate.value
                .defaultValue = connected;
            filter.keyFilters[2].predicates[0]
                .keyFilterPredicate.predicates[1]
                .keyFilterPredicate.value
                .defaultValue = !inactive;
            filter.keyFilters[3].predicates[0]
                .keyFilterPredicate.value
                .defaultValue = selectedGreenhouses;

            filter = JSON.parse(JSON.stringify(
                filter));
            aliasController.updateUserFilter(
            filter);
        }
        filter = JSON.parse(JSON.stringify(filter));
        aliasController.updateUserFilter(filter);
        self.ctx.detectChanges();
        // console.log(filter)
    }
    self.ctx.detectChanges();
}
self.onDataUpdated = function() {}

self.typeParameters = function() {
    return {
        datasourcesOptional: true,
        dataKeysOptional: true
    };
}

self.onResize = function() {}

self.onDestroy = function() {}

