let clearWeatherImg = 'data:image/png;base64,';
let cloudyWeatherImg = 'data:image/png;base64,';

let weatherAPI = 'https://archive-api.open-meteo.com/v1/archive?';
let lat, long, utils, settings, currDay, lastDay;
let weatherByDay = [];
let i = 0

self.onInit = function() {
    utils = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('utils'));
    settings = utils.deepClone(self.ctx.settings) || {};

    currDay = moment().format('YYYY-MM-DD');
    lastDay = moment().add(settings.daysAmount - 1, 'd').format('YYYY-MM-DD');

    self.ctx.$scope.weatherByDay = weatherByDay;
}

self.onDataUpdated = function() {
    weatherByDay = [];
    console.log('TEST');
    console.log(self.ctx)
    self.ctx.data.forEach(el => {
        if (el.data[0]) {
            if (el.dataKey.name === 'latitude') {
                lat = el.data[0][1]
            } else if (el.dataKey.name === 'longitude') {
                long = el.data[0][1]
            }
        }
    })
    if (i < 3) {
        if (lat && long) {
            self.ctx.http.get(
                `${weatherAPI}latitude=${lat}&longitude=${long}&daily=temperature_2m_max,temperature_2m_min,precipitation_sum,windspeed_10m_max,weathercode&windspeed_unit=ms&timezone=Europe%2FLondon&start_date=${currDay}&end_date=${lastDay}&hourly=relativehumidity_2m,surface_pressure`
            ).subscribe(res => {
                for (let i = 0; i < settings.daysAmount; i++) {
                    let newDay = {};
                    newDay.humidity = [];
                    newDay.pressure = [];
                    newDay.minTemp = Math.round(res.daily.temperature_2m_min[i]);
                    newDay.maxTemp = Math.round(res.daily.temperature_2m_max[i]);
                    newDay.time = res.daily.time[i];
                    newDay.windspeed = Math.round(res.daily.windspeed_10m_max[i]);
                    if(res.daily.weathercode[i] > 1) {
                        newDay.img = cloudyWeatherImg;
                    } else {
                        newDay.img = clearWeatherImg;
                    }
                    weatherByDay.push(newDay);
                }
                for(let i = 0; i < res.hourly.time.length; i++) {
                    let day = res.hourly.time[i].split('T')[0];
                    let dayIdx = weatherByDay.findIndex(el => el.time === day);
                    weatherByDay[dayIdx].humidity.push(res.hourly.relativehumidity_2m[i]);
                    weatherByDay[dayIdx].pressure.push(res.hourly.surface_pressure[i]);
                } 
                weatherByDay.forEach(day => {
                    day.humidity = Math.round(day.humidity.reduce((a,b) => a + b) / day.humidity.length);
                    day.pressure = Math.round(day.pressure.reduce((a,b) => a + b) / day.pressure.length);
                })
                self.ctx.$scope.weatherByDay = weatherByDay;
                self.ctx.detectChanges()

            })
        }
        i++
    }


}

self.onResize = function() {}

self.onDestroy = function() {}

