descriptor.controllerScript:
let weatherAPI = 'https://archive-api.open-meteo.com/v1/archive?';
let currDay, utils, settings;
let locationsInfo = [];

function toDate(ts) {
    let date = new Date(ts);
    let month = '' + (date.getMonth() + 1);
    let day = '' + date.getDate();
    let year = date.getFullYear();

    if (month.length < 2)
        month = '0' + month;
    if (day.length < 2)
        day = '0' + day;

return [year, month, day].join('-')
}

self.onInit = function() {
    utils = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('utils'));
    settings = utils.deepClone(self.ctx.settings) || {};
    console.log(settings)

    currDay = toDate(self.ctx.timeWindow.minTime);

    self.ctx.datasources.forEach(gh => {
        let newGH = {
            name: gh.name
        }
        locationsInfo.push(newGH);
    });

    self.ctx.latestData.forEach(data => {
        locationsInfo.forEach(gh => {
            if(data.datasource.entityName === gh.name) {
                gh[data.dataKey.name] = data.data[0][1]
            }
        })
    })
}

self.onDataUpdated = function() {
    let weatherByDay = [];
    currDay = toDate(self.ctx.timeWindow.minTime);
    console.log(currDay)
    locationsInfo.forEach(gh => {
        if(gh.longitude && gh.latitude) {
            self.ctx.http.get(
                `${weatherAPI}latitude=${gh.latitude}&longitude=${gh.longitude}&start_date=${currDay}&end_date=${currDay}&daily=temperature_2m_max,temperature_2m_min,windspeed_10m_max,weathercode&windspeed_unit=ms&timezone=Europe%2FLondon&hourly=relativehumidity_2m,surface_pressure`
            ).subscribe(res => {
                let weatherForSelectedGH = {
                    name: gh.name,
                    time: currDay,
                    minTemp: Math.round(res.daily.temperature_2m_min[0]),
                    maxTemp: Math.round(res.daily.temperature_2m_max[0]),
                    windspeed: Math.round(res.daily.windspeed_10m_max[0]),
                    humidity: [],
                    pressure: [],
                    img: res.daily.weathercode[0] <= 1 ? settings.sunnyWeatherImg
                        : res.daily.weathercode[0] <= 5 ? settings.partlyCloudyWeatherImg
                        : (res.daily.weathercode[0] >= 20 && res.daily.weathercode[0] <= 30) ||
                          (res.daily.weathercode[0] >= 60 && res.daily.weathercode[0] <= 70) ? settings.rainyWeatherImg 
                        : settings.cloudyWeatherImg 
                };

                for(let i = 0; i < res.hourly.time.length; i++) {
                    weatherForSelectedGH.humidity.push(res.hourly.relativehumidity_2m[i]);
                    weatherForSelectedGH.pressure.push(res.hourly.surface_pressure[i]);
                } 

                weatherForSelectedGH.humidity = Math.round(weatherForSelectedGH.humidity.reduce((a,b) => a + b) / weatherForSelectedGH.humidity.length);
                weatherForSelectedGH.pressure = Math.round(weatherForSelectedGH.pressure.reduce((a,b) => a + b) / weatherForSelectedGH.pressure.length);

                weatherByDay.push(weatherForSelectedGH);

                if(weatherByDay.length === locationsInfo.length) {
                    weatherByDay.sort((a, b) => a.name.localeCompare(b.name));
                    self.ctx.$scope.weatherByDay = weatherByDay;
                    self.ctx.detectChanges()
                }
            })
        }
    })


}

self.onResize = function() {}

self.onDestroy = function() {}

self.typeParameters = function() {
    return {
        hasAdditionalLatestDataKeys: true,
        datasourcesOptional: true
    };
}

