let utils, settings, controller, aliasController, filter, range;
let prevStartTs = null;
let prevEndTs = null;
let dateSub;

let ranges = {
    'Today': [moment(), moment()],
    'Yesterday': [moment().subtract(1, 'days'), moment()
        .subtract(1, 'days')
    ],
    'This week': [moment().startOf('isoWeek'),
        moment().endOf('isoWeek')
    ],
    'Last 7 days': [moment().subtract(6, 'days'),
        moment()
    ],
    'Last week': [moment().subtract(1, 'weeks').startOf('isoWeek'), 
        moment().subtract(1, 'weeks').endOf('isoWeek')
    ],
    'This Month': [moment().startOf('month'), moment()
        .endOf('month')
    ],
    'Last Month': [moment().subtract(1, 'month')
        .startOf('month'), moment().subtract(1,
            'month').endOf('month')
    ],
    'This Year': [moment().subtract(0, 'year')
        .startOf('year'), moment()
    ],
    'Last Year': [moment().subtract(1, 'year')
        .startOf('year'), moment().subtract(1,
            'year')
        .endOf('year')
    ]
};

function selectDate(start, end) {
    let startDate = toTimestamp(start._d);
    let endDate = toTimestamp(end._d);
    range = end - start;
    prevEndTs = endDate;
    prevStartTs = startDate;
    updateTimeWindow(startDate, endDate);
    console.log('CHANGE', start, end)
    let chosenRange = start.format('MM-DD-YYYY') + ' - ' +
        end.format('MM-DD-YYYY');
    self.ctx.$scope.chosenRange = chosenRange;
    self.ctx.detectChanges();
}

function updateTimeWindow(startTimeMs, endTimeMs) {
    if (self.ctx.dashboard.dashboardWidgets
        .parentDashboard) {
        if(range < 86400000) {
            self.ctx.dashboard.dashboardWidgets.parentDashboard.onUpdateTimewindow(startTimeMs,endTimeMs, 5400000);
        } else if (range > 86400000) {
            self.ctx.dashboard.dashboardWidgets.parentDashboard.onUpdateTimewindow(startTimeMs,endTimeMs, 43200000);
        }
    } else {
        self.ctx.dashboard
            .onUpdateTimewindow(startTimeMs,
                endTimeMs, 3600000);
    }
}

function toTimestamp(strDate) {
    return Date.parse(strDate);
}

function toDate(ts) {
    let date = new Date(ts);
    let month = '' + (date.getMonth() + 1);
    let day = '' + date.getDate();
    let year = date.getFullYear();

    if (month.length < 2)
        month = '0' + month;
    if (day.length < 2)
        day = '0' + day;

return [month, day, year].join('-')
}

self.onInit = function() {
    utils = self.ctx.$scope.$injector.get(self.ctx
        .servicesMap.get('utils'));
    settings = utils.deepClone(self.ctx.settings) || {};
    controller = self.ctx.stateController;

    self.ctx.$scope.tableTitle = settings.tableTitle;
    console.log('ctx', self.ctx)

    let startDate = toDate(self.ctx.dashboard.dashboardTimewindow.history.fixedTimewindow.startTimeMs);
    let endDate = toDate(self.ctx.dashboard.dashboardTimewindow.history.fixedTimewindow.endTimeMs);

    self.ctx.$scope.chosenRange = startDate + ' - ' + endDate;

    //DateRangePicker
    $('#openButton').daterangepicker({
        ranges: ranges,
        alwaysShowCalendars: true,
        showCustomRangeLabel: false,
        locale: {
            format: 'MM-DD-YYYY',
            firstDay: 1
        },
        parentEl: '#tb-main-layout'
    }, selectDate);

    //datepicker sub
    dateSub = self.ctx.dashboard.dashboardTimewindowChangedSubject.subscribe(data => {
        let startDate = data.history.fixedTimewindow.startTimeMs;
        let endDate = data.history.fixedTimewindow.endTimeMs;
        console.log('CHANGES')
                       console.log('start', endDate, prevStartTs);
        console.log('end', startDate, prevEndTs);
        if (startDate && endDate) {
            if (data.history.historyType === 1) {
                self.ctx.$scope.chosenRange = transformDate(startDate) + ' - ' + transformDate(endDate);
            } else if (data.history.historyType === 0) {
                  self.ctx.$scope.chosenRange = 'interval from main picker';
            }


        self.ctx.detectChanges();
        }
    });
}

function transformDate(date) {
   if (window.moment) {
       return window.moment(date).format('MM-DD-YYYY');
   } else {
       return 'work only in dashboard'
   }
} 

self.actionSources = function() {
    return {
        'elementClick': {
            name: 'widget-action.element-click',
            multiple: true
        }
    };
};

self.onDestroy = function() {
    if (dateSub && dateSub.unsubscribe) {
       dateSub.usubscribe(); 
    }
}

