descriptor.controllerScript:
let utils, settings, controller, aliasController, filter;

self.onInit = function() {
    utils = self.ctx.$scope.$injector.get(self.ctx
        .servicesMap.get('utils'));
    settings = utils.deepClone(self.ctx.settings) || {};
    controller = self.ctx.stateController;

    self.ctx.$scope.tableTitle = settings.tableTitle;
    self.ctx.$scope.buttonText = settings.buttonText;

    //Search
    // dashboard filter params: ${keyName} contains ''
    if (self.ctx.dashboard.dashboardWidgets
        .parentDashboard) {
        aliasController = self.ctx.dashboard
            .dashboardWidgets.parentDashboard
            .aliasController;
    } else {
        aliasController = self.ctx.dashboard
            .aliasController;
    }

    if (aliasController.userFilters && Object.values(aliasController.userFilters).find(el => el.filter === 'Greenhouses Sensors' )) {
        for (let localFilter in aliasController.userFilters) {
            if (aliasController.origFilters[localFilter].filter === 'Greenhouses Sensors') {
            filter = aliasController.userFilters[localFilter];
            filter = JSON.parse(JSON.stringify(filter));
            }
        }
    } else if (aliasController.origFilters) {
        for (let localFilter in aliasController.origFilters) {
            if (aliasController.origFilters[localFilter].filter ==='Greenhouses Sensors') {
                filter = aliasController.origFilters[localFilter];
                filter = JSON.parse(JSON.stringify(filter));
            }
            if (filter) {
                filter.keyFilters[filter.keyFilters.length - 1].key.key = settings.filterField;
                filter = JSON.parse(JSON.stringify(filter));
                aliasController.updateUserFilter(filter);
        }
        }
    }
    self.ctx.$scope.applyFilter = function(event) {
        let filterValue = event.target.value.trim();

        if(filterValue) {
            filter.keyFilters[filter.keyFilters.length - 1].predicates[0].keyFilterPredicate.value.defaultValue = filterValue;
             aliasController.updateUserFilter(filter);
        } else {
            filter.keyFilters[filter.keyFilters.length - 1].predicates[0].keyFilterPredicate.value.defaultValue = '';
            aliasController.updateUserFilter(filter);
        }

        filter = JSON.parse(JSON.stringify(filter));
        aliasController.updateUserFilter(filter);
        self.ctx.detectChanges();
    };

    self.ctx.$scope.onAddClick = function(event) {
        let descriptors = self.ctx.$scope.ctx
            .actionsApi.getActionDescriptors(
                'elementClick');
        let actionEvent;

        if (descriptors.length) {
            actionEvent = descriptors[0];
        }

        setTimeout(() => {
            self.ctx.$scope.ctx.actionsApi
                .handleWidgetAction(null,
                    actionEvent);

        }, 0);
    };
}
self.actionSources = function() {
    return {
        'elementClick': {
            name: 'widget-action.element-click',
            multiple: true
        }
    };
};

self.onDestroy = function() {}

