function updateTimeWindow(startTimeMs, endTimeMs) {
    if (self.ctx.dashboard.dashboardWidgets
        .parentDashboard) {
        if(range < 86400000) {
            self.ctx.dashboard.dashboardWidgets.parentDashboard.onUpdateTimewindow(startTimeMs,endTimeMs, 5400000);
        } else if (range > 86400000) {
            self.ctx.dashboard.dashboardWidgets.parentDashboard.onUpdateTimewindow(startTimeMs,endTimeMs, 43200000);
        }
    } else {
        self.ctx.dashboard
            .onUpdateTimewindow(startTimeMs,
                endTimeMs, 3600000);
    }
}

function toTimestamp(strDate) {
    return Date.parse(strDate);
}

function toDate(ts) {
    let date = new Date(ts);
    let month = '' + (date.getMonth() + 1);
    let day = '' + date.getDate();
    let year = date.getFullYear();

    if (month.length < 2)
        month = '0' + month;
    if (day.length < 2)
        day = '0' + day;

return [month, day, year].join('-')
}

