    $injector = self.ctx.$scope.$injector;
    utils = self.ctx.$scope.$injector.get(self.ctx
        .servicesMap.get('utils'));
    settings = utils.deepClone(self.ctx.settings) || {};

    entityGroupService = $injector.get(self.ctx.servicesMap.get('entityGroupService'));

    let companiesArr;
    entityGroupService.getEntityGroups('CUSTOMER').subscribe(res => {
        if(res.length) {
            res.forEach(entity => {
                if(entity.name === 'Location Dashboard') {
                    entityGroupService.getEntityGroupEntities(entity.id.id, self.ctx.pageLink(100, 0)).subscribe(
                            companies => {
                                if(companies.data.length > 0) {
                                    self.ctx.$scope.companies = companies.data;
                                }
                            }
                        )
                }
            })
        }
    })

    self.ctx.$scope.tableTitle = settings.tableTitle;

    //Search
    // dashboard filter params: ${keyName} contains ''
    if (self.ctx.dashboard.dashboardWidgets
        .parentDashboard) {
        aliasController = self.ctx.dashboard.parentDashboard.aliasController;
    } else {
        aliasController = self.ctx.dashboard
            .aliasController;
    }

   if (aliasController.userFilters &&  Object.values(aliasController.userFilters).find(el => el.filter === 'Search Filter' )) {
        for (let localFilter in aliasController.userFilters) {
            if (aliasController.origFilters[localFilter].filter === 'Search Filter') {
            filter = aliasController.userFilters[localFilter];
            filter = JSON.parse(JSON.stringify(filter));
            }
        }
    } else if (aliasController.origFilters) {
        for (let localFilter in aliasController.origFilters) {
            if (aliasController.origFilters[localFilter].filter ==='Search Filter') {
                filter = aliasController.origFilters[localFilter];
                filter = JSON.parse(JSON.stringify(filter));
            }
            if (filter) {
                filter.keyFilters[0].key.key = settings.filterField;
                filter = JSON.parse(JSON.stringify(filter));
                aliasController.updateUserFilter(filter);
        }
        }
    }
    self.ctx.$scope.applyFilter = function(event) {
        let filterValue = event.target.value.trim();

            if (filterValue) {
                filter.keyFilters[0].predicates[0].keyFilterPredicate.value.defaultValue = filterValue;
                aliasController.updateUserFilter(filter);

            } else {
                filter.keyFilters[0].predicates[0].keyFilterPredicate.value.defaultValue = '';
                aliasController.updateUserFilter(filter);
            }

        filter = JSON.parse(JSON.stringify(filter));
        aliasController.updateUserFilter(filter);
        self.ctx.detectChanges();
    };

    self.ctx.$scope.selectCompany = function(companyName) {
        if(companyName) {
            filter.keyFilters[1].predicates[0].keyFilterPredicate.value.defaultValue = companyName;
            aliasController.updateUserFilter(filter);
        } else {
            filter.keyFilters[0].predicates[0].keyFilterPredicate.value.defaultValue = '';
            aliasController.updateUserFilter(filter);
        }

        filter = JSON.parse(JSON.stringify(filter));
        aliasController.updateUserFilter(filter);
        self.ctx.detectChanges();
        self.ctx.updateAliases();
    }
}
